import React from 'react';

export const Button = ({ children, variant = 'primary', className = '', ...props }) => (
  <button
    className={`inline-flex items-center justify-center px-4 py-2 text-sm font-medium rounded-md shadow-sm transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 ${
      variant === 'primary'
        ? 'text-white bg-blue-600 hover:bg-blue-700 focus:ring-blue-500'
        : variant === 'outline'
        ? 'text-blue-600 border border-blue-600 hover:bg-blue-50 focus:ring-blue-500'
        : 'text-gray-700 bg-white hover:bg-gray-50'
    } ${className}`}
    {...props}
  >
    {children}
  </button>
);

export const Select = ({ children, className = '', ...props }) => (
  <select
    className={`mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md ${className}`}
    {...props}
  >
    {children}
  </select>
);

export const Input = ({ className = '', ...props }) => (
  <input
    className={`mt-1 block w-full shadow-sm sm:text-sm rounded-md border-gray-300 focus:ring-blue-500 focus:border-blue-500 ${className}`}
    {...props}
  />
);

export const Label = ({ children, htmlFor, className = '' }) => (
  <label htmlFor={htmlFor} className={`block text-sm font-medium text-gray-700 ${className}`}>
    {children}
  </label>
);
