import React from 'react';
import { Select, Input, Label, Button } from '../components/ui';

const MedicineForm = ({ medicine, index, onChange, onRemove, options }) => {
  return (
    <div className="border p-4 rounded-lg mb-4 bg-gray-50">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-3">
        <div>
          <Label>Medicine Name</Label>
          <Select
            value={medicine.name}
            onChange={(e) => onChange(index, 'name', e.target.value)}
            required
          >
            <option value="">Select Medicine</option>
            {options.medicines.map((med) => (
              <option key={med} value={med}>{med}</option>
            ))}
          </Select>
        </div>
        <div>
          <Label>Dosage</Label>
          <Select
            value={medicine.dosage}
            onChange={(e) => onChange(index, 'dosage', e.target.value)}
            required
          >
            <option value="">Select Dosage</option>
            {options.dosages.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </Select>
        </div>
      </div>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-3">
        <div>
          <Label>Frequency</Label>
          <Select
            value={medicine.frequency}
            onChange={(e) => onChange(index, 'frequency', e.target.value)}
            required
          >
            {options.frequencies.map((f) => (
              <option key={f} value={f}>{f}</option>
            ))}
          </Select>
        </div>
        <div>
          <Label>Timing</Label>
          <Select
            value={medicine.timing}
            onChange={(e) => onChange(index, 'timing', e.target.value)}
            required
          >
            {options.timings.map((t) => (
              <option key={t} value={t}>{t}</option>
            ))}
          </Select>
        </div>
        <div>
          <Label>Duration</Label>
          <Input
            type="text"
            value={medicine.duration}
            onChange={(e) => onChange(index, 'duration', e.target.value)}
            placeholder="e.g., 7 days"
            required
          />
        </div>
      </div>
      
      <div className="mb-3">
        <Label>Special Instructions</Label>
        <Input
          type="text"
          value={medicine.instructions || ''}
          onChange={(e) => onChange(index, 'instructions', e.target.value)}
          placeholder="Any special instructions"
        />
      </div>
      
      {onRemove && (
        <Button
          type="button"
          onClick={() => onRemove(index)}
          variant="outline"
          className="mt-2"
        >
          Remove Medicine
        </Button>
      )}
    </div>
  );
};

export default MedicineForm;
